mapper.update:
update org_post
set is_delete = 2,#{updateTime,jdbcType=BIGINT}
where id=#{id}

mapper.select:
select id, org_id as orgId, post_name as postName, post_type as postType, create_time as createTime from org_post where org_id=#{orgId} and post_type BETWEEN 2 and 3 and is_delete=1
and id != #{id}
ORDER BY create_time desc

mapper.update:
update org_post
set is_delete = 2,
update_time = #{updateTime,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}
