mapper.select:
SELECT
FROM user_info WHERE is_delete = 1 AND id IN
#{item}

mapper.update:
update user_info
	set is_delete = 2,
	update_time = #{updateTime,jdbcType=BIGINT}		
where id = #{id,jdbcType=BIGINT}

update user_info
set is_delete = 2,
update_time = #{updateTime,jdbcType=BIGINT}
where user_name = #{userName,jdbcType=VARCHAR}

mapper.select:
select

from user_info where 1=1 and user_type = 2

	and id = #{id,jdbcType=BIGINT}


	and user_name LIKE concat("%",#{userName,jdbcType=VARCHAR},"%")


	and mobile_phone LIKE concat("%",#{mobilePhone,jdbcType=VARCHAR},"%")


	and create_time >= #{startTime,jdbcType=BIGINT}


	and #{endTime,jdbcType=BIGINT} >= create_time


	and is_delete = #{isDelete,jdbcType=TINYINT}


	and name like concat("%",#{name,jdbcType=VARCHAR},"%")


	and is_forbid = #{isForbid,jdbcType=TINYINT}


	and is_active = #{isActive,jdbcType=TINYINT}

order by create_time desc
limit #{startIndex},#{pageSize}

SELECT COUNT(1) from user_info u
LEFT JOIN org_staff staff on staff.user_id = u.id 
where staff.top_org_id = #{topOrgId, jdbcType=BIGINT}

select
count(1)
from user_info where 1=1 and user_type = 2

	and id = #{id,jdbcType=BIGINT}


	and user_name = #{userName,jdbcType=VARCHAR}


	and mobile_phone = #{mobilePhone,jdbcType=VARCHAR}


	and create_time >= #{startTime,jdbcType=BIGINT}


	and #{endTime,jdbcType=BIGINT} >= create_time


	and is_delete = #{isDelete,jdbcType=TINYINT}


	and name = #{name,jdbcType=VARCHAR}


	and is_forbid = #{isForbid,jdbcType=TINYINT}


	and is_active = #{isActive,jdbcType=TINYINT}
